    @Override
    public void OnServiceItemClick(Service category) {

        Bundle bundle=new Bundle();
        bundle.putString("IMAGE",category.getImage());
        bundle.putString("NAME",category.getName());
        bundle.putString("DESCRIPTION",category.getDescription());
        bundle.putString("PRICE",category.getPrice());
       // Toast.makeText(Home.this,"You have clicked "+category.getName(), Toast.LENGTH_LONG).show();

        Intent intent=new Intent(Home.this,ServiceDetails.class);
        intent.putExtras(bundle);
        startActivity(intent);
    }
